import React from "react";
import { useLocation, useNavigate } from "react-router-dom";

const Result = () => {
  const navigate = useNavigate();
  const { state } = useLocation();
  const { result, confidence, image } = state || {};  // Update to use 'result' instead of 'deficiency'

  const imageUrl = image ? `/uploads/${image}` : null;
  const age = localStorage.getItem("age");

  // Debugging: log the state to check if deficiency is available
  console.log("Result Page State:", state); 

  const handleRecommendationClick = () => {
    // Check if the result (deficiency) is available
    if (!result) {
      alert("Prediction result is missing!");
      return;
    }
    
    // Proceed to the recommendation page with the correct state
    navigate("/recommendation", {
      state: { deficiency: result, confidence, image, age }, // Pass 'result' as deficiency
    });
  };

  return (
    <div className="min-h-screen bg-blue-50 flex flex-col items-center justify-center px-4 py-10">
      <h1 className="text-3xl font-bold text-blue-700 mb-8 text-center">
        Prediction Result
      </h1>

      <div className="bg-white border border-gray-200 rounded-xl shadow-md w-full max-w-2xl p-6">
        <div className="mb-6">
          <p className="text-lg font-semibold text-gray-700 mb-2">
            <span className="text-blue-600">Detected Deficiency:</span>{" "}
            <span className="text-xl text-blue-700 font-bold">
              {result} {/* Update to display 'result' instead of 'deficiency' */}
            </span>
          </p>
          <p className="text-lg text-gray-700">
            <span className="text-green-600 font-medium">
              Confidence Score:
            </span>{" "}
            <span className="font-semibold">{confidence}%</span>
          </p>
        </div>

        {imageUrl && (
          <div className="rounded-lg overflow-hidden border border-blue-100 shadow mb-6">
            <img
              src={imageUrl}
              alt="Uploaded"
              className="w-full object-cover max-h-[400px]"
            />
          </div>
        )}

        <button
          onClick={handleRecommendationClick} // Use the handler here
          className="w-full bg-blue-600 text-white font-medium py-2 rounded-md hover:bg-blue-700 transition"
        >
          View Recommendations
        </button>
      </div>
    </div>
  );
};

export default Result;
