import React from "react";

const recommendations = [
  {
    title: "Vitamin A Deficiency",
    color: "bg-orange-50",
    suggestions: [
      "Eat carrots, spinach, sweet potatoes, and liver.",
      "Add more green leafy vegetables to your diet.",
      "Avoid excessive alcohol—it depletes Vitamin A.",
      "Include fortified dairy and egg yolks in meals.",
    ],
  },
  {
    title: "Vitamin B12 Deficiency",
    color: "bg-purple-50",
    suggestions: [
      "Consume more fish, poultry, eggs, and milk.",
      "Consider B12 supplements if you're vegetarian.",
      "Avoid antacids—they interfere with absorption.",
      "Include fortified cereals and plant-based milk.",
    ],
  },
  {
    title: "Vitamin D Deficiency",
    color: "bg-yellow-50",
    suggestions: [
      "Spend 15–20 mins in sunlight daily.",
      "Include oily fish like salmon and tuna.",
      "Consume fortified milk and orange juice.",
      "Take Vitamin D3 supplements if needed.",
    ],
  },
  {
    title: "Zinc Deficiency",
    color: "bg-blue-50",
    suggestions: [
      "Eat shellfish, meat, beans, and nuts.",
      "Avoid excessive iron—it competes with zinc.",
      "Add dairy, whole grains, and pumpkin seeds.",
      "Limit alcohol intake—it hinders zinc absorption.",
    ],
  },
];

const Recommendation = () => {
  return (
    <div className="min-h-screen px-4 py-10 bg-white text-gray-800">
      <h1 className="text-3xl font-bold text-blue-700 text-center mb-10">
        Recommended Health Advice for Deficiencies
      </h1>

      <div className="grid grid-cols-1 sm:grid-cols-2 gap-6 max-w-6xl mx-auto">
        {recommendations.map((rec, idx) => (
          <div
            key={idx}
            className={`rounded-xl shadow-md p-6 border ${rec.color}`}
          >
            <h2 className="text-xl font-semibold text-blue-800 mb-4">
              {rec.title}
            </h2>
            <ul className="list-disc pl-5 space-y-2 text-base">
              {rec.suggestions.map((tip, i) => (
                <li key={i}>{tip}</li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Recommendation;


