import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import axios from "../services/axios"; // Assuming you have axios set up

const Recommendation = () => {
    const { state } = useLocation();
    const { result, confidence, image, age } = state || {};
    const [recommendations, setRecommendations] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchRecommendations = async () => {
            try {
                // Make the request to Gemini API with the deficiency (result)
                const response = await axios.post("http://gemini-api-endpoint.com/recommendations", {
                    deficiency: result, // Using 'result' to get recommendations
                    age: age, // Optionally, pass the age if Gemini requires it
                });

                // Set recommendations data from the response
                setRecommendations(response.data);
                setLoading(false);
            } catch (err) {
                console.error("Error fetching recommendations:", err);
                setError("Failed to load recommendations. Please try again.");
                setLoading(false);
            }
        };

        if (result) {
            fetchRecommendations(); // Fetch recommendations when result is available
        }
    }, [result, age]);

    return (
        <div className="min-h-screen bg-blue-50 flex flex-col items-center justify-center px-4 py-10">
            <h1 className="text-3xl font-bold text-blue-700 mb-8 text-center">Recommendations</h1>

            {loading && (
                <p className="text-lg text-blue-600">Loading recommendations...</p>
            )}

            {error && (
                <p className="text-lg text-red-600">{error}</p>
            )}

            {recommendations && !loading && !error && (
                <div className="bg-white border border-gray-200 rounded-xl shadow-md w-full max-w-2xl p-6">
                    <h2 className="text-2xl font-semibold text-blue-700 mb-4">Recommended Actions</h2>
                    <ul className="list-disc pl-6 space-y-2">
                        {recommendations.map((item, index) => (
                            <li key={index} className="text-lg text-gray-700">
                                {item.recommendation} {/* Customize based on the response structure */}
                            </li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
};

export default Recommendation;
